package purejava;

public class JavaCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a = (byte)128;
		System.out.println(a); // -128
		byte b = (byte)255;
		System.out.println(b); // -1
		byte c = (byte)767;
		System.out.println(c); // -1
		byte d = (byte)1023;
		System.out.println(d); // -1
	}

}
